function Screen1_js() {

    /* Object & array with components "name-to-id" mapping */
    var n2id_buf = {
        'mobilebutton_2': 'Screen1_mobilebutton_2',
        'mobilelabel_3': 'Screen1_mobilelabel_3',
        'mobilelabel_4': 'Screen1_mobilelabel_4',
        'mobilelabel_5': 'Screen1_mobilelabel_5'
    };

    if ("n2id" in window && window.n2id !== undefined) {
        $.extend(n2id, n2id_buf);
    } else {
        window.n2id = n2id_buf;
    }

    /*
     * Nonvisual components
     */

    Apperyio.mappings = Apperyio.mappings || {};

    Apperyio.mappings["Screen1_mobilebarcode1_onsuccess_mapping_0"] = {
        "homeScreen": "Screen1",
        "directions": [

        {
            "from_name": "mobilebarcode1",
            "from_type": "SERVICE_RESPONSE",

            "to_name": "Screen1",
            "to_type": "UI",

            "mappings": [

            {

                "source": "$['data']['text']",
                "target": "$['mobilelabel_3:text']"

            },

            {

                "source": "$['data']['format']",
                "target": "$['mobilelabel_4:text']"

            },

            {

                "source": "$['data']['cancelled']",
                "target": "$['mobilelabel_5:visible']"

            }

            ]
        }

        ]
    };

    Apperyio.mappings["Screen1_mobilebarcode1_onbeforesend_mapping_0"] = {
        "homeScreen": "Screen1",
        "directions": []
    };

    Apperyio.datasources = Apperyio.datasources || {};

    window.mobilebarcode1 = Apperyio.datasources.mobilebarcode1 = new Apperyio.DataSource(BarcodeService, {
        "onBeforeSend": function(jqXHR) {
            Apperyio.processMappingAction(Apperyio.mappings["Screen1_mobilebarcode1_onbeforesend_mapping_0"]);
        },
        "onComplete": function(jqXHR, textStatus) {

            Apperyio.refreshScreenFormElements("Screen1");
        },
        "onSuccess": function(data) {
            Apperyio.processMappingAction(Apperyio.mappings["Screen1_mobilebarcode1_onsuccess_mapping_0"]);
        },
        "onError": function(jqXHR, textStatus, errorThrown) {
            alert("Scan has fail please try again");
        }
    });

    Apperyio.CurrentScreen = 'Screen1';
    _.chain(Apperyio.mappings).filter(function(m) {
        return m.homeScreen === Apperyio.CurrentScreen;
    }).each(Apperyio.UIHandler.hideTemplateComponents);

    /*
     * Events and handlers
     */

    // On Load
    var Screen1_onLoad = function() {
            Screen1_elementsExtraJS();

            Screen1_deviceEvents();
            Screen1_windowEvents();
            Screen1_elementsEvents();
        };

    // screen window events


    function Screen1_windowEvents() {

        $('#Screen1').bind('pageshow orientationchange', function() {
            var _page = this;
            adjustContentHeightWithPadding(_page);
        });

    };

    // device events


    function Screen1_deviceEvents() {
        document.addEventListener("deviceready", function() {

        });
    };

    // screen elements extra js


    function Screen1_elementsExtraJS() {
        // screen (Screen1) extra code

    };

    // screen elements handler


    function Screen1_elementsEvents() {
        $(document).on("click", "a :input,a a,a fieldset label", function(event) {
            event.stopPropagation();
        });

        $(document).off("click", '#Screen1_mobilecontainer [name="mobilebutton_2"]').on({
            click: function(event) {
                if (!$(this).attr('disabled')) {
                    try {
                        mobilebarcode1.execute({});
                    } catch (e) {
                        console.error(e);
                        hideSpinner();
                    };

                }
            },
        }, '#Screen1_mobilecontainer [name="mobilebutton_2"]');

    };

    $(document).off("pagebeforeshow", "#Screen1").on("pagebeforeshow", "#Screen1", function(event, ui) {
        Apperyio.CurrentScreen = "Screen1";
        _.chain(Apperyio.mappings).filter(function(m) {
            return m.homeScreen === Apperyio.CurrentScreen;
        }).each(Apperyio.UIHandler.hideTemplateComponents);
    });

    Screen1_onLoad();
};

$(document).off("pagecreate", "#Screen1").on("pagecreate", "#Screen1", function(event, ui) {
    Apperyio.processSelectMenu($(this));
    Screen1_js();
});